import React,{useState, useEffect} from 'react';
import { View,Text, TouchableOpacity, StyleSheet,Modal } from 'react-native';
import { ProgressBar, Colors } from 'react-native-paper';

import { useNavigation } from '@react-navigation/native';

export default function Desafio1({route}) {
  
  const navigation = useNavigation();
  var rr =0;
  var v =0;

  const [segundos, setSegundos] = useState(61);
  
  //vetor que Ã© preenchido com as variaveis pseudo aleatÃ³rias
  const [grade, setGrade] = useState([]);
  
  const [atualiza, setAtualiza] = useState(false);

 //Variaveis que recebem os numeros pseudo aleatÃ³rios
  const [x, setX] = useState(0);
  const [x1, setX1] = useState(0);
  const [x2, setX2] = useState(0);
  const [x3, setX3] = useState(0);
  const [x4, setX4] = useState(0);
  const [x5, setX5] = useState(0);
  const [x6, setX6] = useState(0);
  const [x7, setX7] = useState(0);
  const [x8, setX8] = useState(0);
  const [x9, setX9] = useState(0);
  const [x10, setX10] = useState(0);
  const [x11, setX11] = useState(0);

  //variaveis que ajudam com a validaÃ§Ã£o dos estilos
  const [sty, setSty] = useState(false);
  const [sty1, setSty1] = useState(false);
  const [sty2, setSty2] = useState(false);
  const [sty3, setSty3] = useState(false);
  const [sty4, setSty4] = useState(false);
  const [sty5, setSty5] = useState(false);
  const [sty6, setSty6] = useState(false);
  const [sty7, setSty7] = useState(false);
  const [sty8, setSty8] = useState(false);
  const [sty9, setSty9] = useState(false);
  const [sty10, setSty10] = useState(false);
  const [sty11, setSty11] = useState(false);

  //jogadores
  const [jogador1, setJogador1] = useState(true);
  const [jogador2, setJogador2] = useState(false);

  //contadores de puntuaÃ§Ã£o
  const [pontos1, setPontos1] = useState(0);
  const [pontos2, setPontos2] = useState(0);

  //variaveis que recebem o valor do "click" para somar
  const [n1, setN1] = useState(0);
  const [n2, setN2] = useState(0);

  //variavel que marca o "tempo de jogo", varia de 0 a 1, incrementando 0.001 a cada 100 ms
  const [numero, setNumero] = useState(0);
  
  //variavel booleana que chama um Modal para dar inicio ao jogo
  const [chamaModalinicio, setChamaModalinicio] = useState(true);

  //String que recebe o  vencedor e Ã© enviada para proxima tela(Final) indicando quem ganhou
  const [vencedor, setVencedor] = useState('');

  //variaveis que ajudam com o controle da estilizaÃ§Ã£o
  const [visivel, setVisivel] = useState(false);
  const [visivel1, setVisivel1] = useState(false);
  const [visivel2, setVisivel2] = useState(false);
  const [visivel3, setVisivel3] = useState(false);
  const [visivel4, setVisivel4] = useState(false);
  const [visivel5, setVisivel5] = useState(false);
  const [visivel6, setVisivel6] = useState(false);
  const [visivel7, setVisivel7] = useState(false);
  const [visivel8, setVisivel8] = useState(false);
  const [visivel9, setVisivel9] = useState(false);
  const [visivel10, setVisivel10] = useState(false);
  const [visivel11, setVisivel11] = useState(false);

 useEffect(() => { //Effect 1 usado para atualizar a grade(vetor com os numeros do jogo), 
                   //quando a grade nÃ£o tem mais combinaÃ§Ãµes que somam 10 a variavel "atualiza" se torna 'true',
                   //gera novos numeros, volta as cores pro padrao, acaba de zerar a grade caso ainda tenha numeros
                   //e preenche novamente com novos numeros aleatÃ³rios.
     if(atualiza == true){
      getRandomIntInclusive(); 

      grade.splice(0,grade.length);

      if(grade.length == 0){ //zerar array antes de colocar novos valores
            zeraCores();
            Grades(); 
          }

       setAtualiza(false);
    }
    
  },[grade.length]);      

  useEffect(() => {  //Esse Effect 2 cuida do tempo que os jogadores tem para escolher os pares,
                     //a variavel "numero" incrementa 0.005 a cada 100ms atÃ© valer 1 utilizando a funÃ§Ã£o 'setInterval' do javascript
                     //alem de incrementar os pontos de cada jogador a cada par escolhido.
                     //quando o "numero" chega a 1, ele retorna o "vencedor" e enviar pra proxima tela(Final)
    if(n1 > 0 && n2 > 0){
    incrementePonto();
    }
    if(numero <= 0.9){
     const timer= setInterval( () => {
     setNumero(numero + 0.015);
     setSegundos(segundos - 1);
    },900);
    
    return () => clearInterval(timer);
    }else{
        ganhador();
       let vence = vencedor;
       let pt1 = pontos1;
       let pt2 = pontos2;
       navigation.navigate("Desafio1_1", {vence , pt1, pt2});
       
        
    }

  
   
});

useEffect(() => { //Este Effect 3 garante que os valores da grade estarÃ£o corretos, comparando o primeiro valor
                  //do vetor(grade) com o primeiro valor da matriz que Ã© exibida para os jogadores, 
                  //se nÃ£o for o mesmo valor, ele zera o vetor e preenche novamente.
  if (grade[0] != x){
    grade.splice(0,grade.length);
    Grades();
  }
  if(grade.length == 0){ //esta condiÃ§Ã£o Ã© para casos em que a Thread falha ao preencher a grade.
    Grades(); 
  } 
},[x]);

function iteraArray(){ //esta funÃ§Ã£o verifica toda as combinaÃ§Ãµes que resultam em 10 e incrementa o contador "v",
                       //cada iteraÃ§Ã£o o vetor(grade) fica menor, pois, quando o usuario escolhe um numero, ele Ã©
                       //retirado do vetor(grade), dessa forma, quando o contador chega a 0 significa que nao a mais
                       //pares que somam 10. Em seguida muda o valor de "atualiza" para true, que Ã© chamado la no Effect 1
                       //para atualizar o vetor(grade) e dar continuidade ao jogo atÃ© que o Effect 2 acabe a execuÃ§Ã£o.
  for(var i =0; i <=grade.length; i++){
    for(var j =0; j <=grade.length; j++){
       if(((grade[i] + grade[j]) == 10) && (j !== i) ){
          v = v + 1;    
       }
    }
  }

  if(((v/2) == 0) && ((grade.length % 2) == 0)){
    grade.splice(0,grade.length); 
    setAtualiza(true);
    v=0;

  }

}


function Grades(){ //funÃ§Ã£o que popula o vetor(grade)
   setGrade(grade.concat(x).concat(x1).concat(x2).concat(x3).concat(x4).concat(x5).concat(x6).concat(x7).concat(x8).concat(x9).concat(x10).concat(x11)); 
 
}
   
 function ganhador(){ //funÃ§Ã£o que verifica o jogador que mais pontuou ou se ouve empate
   if(pontos1 > pontos2){
      setVencedor('Jogador 1 Venceu!!!');
   }else if(pontos1 < pontos2){
     setVencedor('Jogador 2 Venceu!!!');
   }else{
     setVencedor('Empate!')
   }
 }
  function getRandomIntInclusive(min, max) { //funÃ§Ã£o que gera os numeros pseudo aleatorios
    min = Math.ceil(1);
    max = Math.floor(9);
    return (setX(Math.floor(Math.random() * (max - min + 1)) + min),
            setX1(Math.floor(Math.random() * (max - min + 1)) + min),
            setX2(Math.floor(Math.random() * (max - min + 1)) + min),
            setX3(Math.floor(Math.random() * (max - min + 1)) + min),
            setX4(Math.floor(Math.random() * (max - min + 1)) + min),
            setX5(Math.floor(Math.random() * (max - min + 1)) + min),
            setX6(Math.floor(Math.random() * (max - min + 1)) + min),
            setX7(Math.floor(Math.random() * (max - min + 1)) + min),
            setX8(Math.floor(Math.random() * (max - min + 1)) + min),
            setX9(Math.floor(Math.random() * (max - min + 1)) + min),
            setX10(Math.floor(Math.random() * (max - min + 1)) + min),
            setX11(Math.floor(Math.random() * (max - min + 1)) + min)
           
           );
  }

  function zeraCores(){ //funÃ§Ã£o que tira as cores dos numeros escolhidos pelos jogadores e volta ao padrao
    setVisivel(false);
    setVisivel1(false);
    setVisivel2(false);
    setVisivel3(false);
    setVisivel4(false);
    setVisivel5(false);
    setVisivel6(false);
    setVisivel7(false);
    setVisivel8(false);
    setVisivel9(false);
    setVisivel10(false);
    setVisivel11(false);

    setSty(false);
    setSty1(false);
    setSty2(false);
    setSty3(false);
    setSty4(false);
    setSty5(false);
    setSty6(false);
    setSty7(false);
    setSty8(false);
    setSty9(false);
    setSty10(false);
    setSty11(false);
  }

function incrementePonto(){ //funÃ§Ã£o que atribui cada ponto ao devido jogador, ela verifica se os pares somam 10 e atribui ao jogador corrente
  let res = n1+n2;
  
  
  if((res == 10) && (jogador1 == true)){
   setPontos1(pontos1 + 1);
   setJogador1(false);
   setJogador2(true);
  
    
    }else if((res == 10) && (jogador2 == true)){
      setPontos2(pontos2 + 1);
      setJogador1(true);
      setJogador2(false);

        } else if(((res > 0)&&(res != 10)) && (jogador1 == true)){
        
        setJogador1(false);
        setJogador2(true);

          }else if(((res > 0)&&(res != 10)) && (jogador2 == true)){
            
            setJogador1(true);
            setJogador2(false);
            }
   
   setN1(0);
   setN2(0);
   res=0;

}  

function executa (n){//funÃ§Ã£o que recebe o valor clicado pelo jogador, retira do vetor(grade), muda a cor do
                     //botÃ£o para a cor do jogadore chama a funÃ§Ã£o incrementa ponto caso seja o segundo numero clicado
    
  if(grade.length == 0){
     Grades();
  }

  let pos = grade.indexOf(n); //identifica a posiÃ§Ã£o do numero no vetor(grade)
  grade.splice(pos,1); //retira o numero do vetor(grade)
 
  if(grade.length <= 6 ){ 
    iteraArray(); //funÃ§Ã£o que verifica as combinaÃ§Ãµes, sÃ³ chamada quando o vetor esta pela metade.
  }

 switch (n) { //ambos switch servem para controlar as cores dos numeros
    case x: setVisivel(true) ;   
        break;  
    case x1: setVisivel1(true);   
        break; 
    case x2: setVisivel2(true) ;   
        break; 
    case x3: setVisivel3(true) ;   
        break; 
    case x4: setVisivel4(true) ;   
        break; 
    case x5: setVisivel5(true) ;   
        break; 
    case x6: setVisivel6(true) ;   
        break; 
    case x7: setVisivel7(true) ;   
        break; 
     
}
  
  if(jogador1 == true){

    switch (n) {
        case x: setSty(true);   
            break; 
        case x1: setSty1(true);   
            break; 
        case x2: setSty2(true);   
            break; 
        case x3: setSty3(true);   
            break; 
        case x4: setSty4(true);   
            break; 
        case x5: setSty5(true);   
            break; 
        case x6: setSty6(true);   
            break; 
        case x7: setSty7(true);   
            break; 
        
      
    }

  }

 if(n1 == 0 ){ //condiÃ§Ã£o que seta o par para soma
    setN1(n);
    }else{
        setN2(n);
        }


  if((n1 !== 0)&&(n2 !== 0)){
   incrementePonto(); //funÃ§Ã£o que verifica se o par soma 10. sÃ³ Ã© chamada quando o usuarioa clica em 2 numeros.
  }

}

function executa1 (n){ //FunÃ§Ã£o cÃ³pia, porÃ©m, para a linha 3 da matriz exibida, pois sÃ£o valores repetidos e apresentariam problemas com a estilizaÃ§Ã£o
