import React,{useState} from 'react';
import { View,Text, TouchableOpacity,FlatList, StyleSheet,Modal } from 'react-native';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';

export default function Desafios() {
    const [isModal, setIsModal] = useState(false);
    const navigation = useNavigation();

    function chamaModal(){
        setIsModal(true);
    }
 return (
   <View style={styles.container}>
      < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Desafios</Text>
       </Animatable.View>

       <Text style={styles.txtEscolha}>Escolha um grupo para aplicar desafios</Text>

       <Animatable.View animation="zoomIn" style={styles.viewFlatList}>
           <FlatList/>
       </Animatable.View>

       <Animatable.View animation="zoomIn" style={styles.viewBtn}>
           <TouchableOpacity style={styles.btnIniciar}onPress={() => chamaModal()}>
             <Text style={styles.btnIniciarTxt}>Avançar</Text>
           </TouchableOpacity>
       </Animatable.View>

       <Modal animationType='fadeIn' visible={isModal}>
           <View style={styles.viewModal}>
               <Text style={styles.titleTxtModal}>Escolha um desafio</Text>

               <TouchableOpacity style={styles.btnDesafioModal}>
                   <Text style={styles.btnTxtModal}>Clear the Deck</Text>
               </TouchableOpacity>

               <TouchableOpacity style={styles.btnDesafioModal}>
                   <Text style={styles.btnTxtModal}>Chocolate Chili Chellenge</Text>
               </TouchableOpacity>

               <TouchableOpacity style={styles.btnDesafioModal}>
                   <Text style={styles.btnTxtModal}>Ten Frame</Text>
               </TouchableOpacity>

           </View>
       </Modal>
      

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#38a69d',
       },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewFlatList:{
        flex:4,
        margin:'5%',
        backgroundColor:'#fff',
        borderRadius:10
    },
    viewBtn:{
        flex:1,
        alignItems:'center',
        marginBottom:-25
    },
    viewBtn2:{
        flex:1,
        alignItems:'center',
        
    },
    btnIniciar:{
        backgroundColor:'#fff',
        alignItems:'center',
        justifyContent:'center',
        width:'90%',
        height:40,
        borderRadius:10
    },
    btnIniciarTxt:{
        fontSize:18,
        fontWeight:'bold',
        color:'#38a69d'
    },
    txtEscolha:{
        textAlign:'center',
        fontSize:14,
        fontWeight:'bold',
        color:'#fff',
        marginBottom:-10
    },
    viewModal:{
        flex: 1,
        alignItems:'center',
        justifyContent:'center',
        backgroundColor:'#38a69d'
    },
    titleTxtModal:{
        marginBottom:15,
        fontSize:25,
        fontWeight:'bold',
        color: "#fff"
    },
    btnDesafioModal:{
        width: 250,
        height: 50,
        backgroundColor:'#fff',
        borderRadius:15,
        alignItems:'center',
        justifyContent:'center',
        marginTop:10
    },
    btnTxtModal:{
        color: '#38a69d',
        fontSize:20
    }
})
