import React from 'react';
import { View,
   Text,
   StyleSheet,
  Image,
 TouchableOpacity } from 'react-native';

 import * as Animatable from 'react-native-animatable';

 import { useNavigation } from '@react-navigation/native';

export default function Welcome() {
  const navigation = useNavigation();
 return (
   <View style={styles.container}>
       <View style={styles.containerLogo}>
         <Animatable.Image
         animation="flipInY"
          source={require('../../../src/assets/discalculiahe.png')}
          style={{width:'100%'}}
          resizeMode="contain"
         />
       </View>
      

       <Animatable.View delay={500} animation="fadeInUp" style={styles.containerForm}>
         <Text style={styles.title}>Descubra se há posibilidade de desenvolver Descalculia!</Text>
         <Text style={styles.txt}>Faça loguin para começar</Text>

         <TouchableOpacity 
         onPress={() => navigation.navigate('SignIn')}
         style={styles.button}>
           <Text style={styles.buttonTxt}>Acessar</Text>
         </TouchableOpacity>
       </Animatable.View>
   </View>
  );
}

const styles = StyleSheet.create({
  container:{
   flex:1,
   backgroundColor: '#38a69d'
  },

  containerLogo:{
    flex:2,
    backgroundColor: '#38a69d',
    justifyContent:'center',
    alignItems:'center'
  },
  containerForm:{
    flex:1,
    backgroundColor: '#fff',
    borderTopRightRadius:25,
    borderTopLeftRadius:25,
    paddingStart:'5%',
    paddingEnd:'5%'
  },
  title:{
    fontSize:24,
    fontWeight:'bold',
    marginTop:28,
    marginBottom:12,
  },
  txt:{
    color:'#a1a1a1'
  },
  button:{
    position:'absolute',
    backgroundColor:'#38a69d',
    borderRadius:50,
    paddingVertical: 8,
    width:'60%',
    alignSelf:'center',
    bottom:'15%',
    alignItems:'center',
    justifyContent:'center'
  },
  buttonTxt:{
    fontSize:18,
    color:'#fff',
    fontWeight:'bold'
  }

})
