import React,{useContext,useState,useEffect} from 'react';
import { View,Text,StyleSheet, TouchableOpacity } from 'react-native';

import * as Animatable from 'react-native-animatable';
import { useNavigation } from '@react-navigation/native';

import { AuthContext } from '../../../../contexts/auth';

export default function Teste4_2_2() {

  const navigation = useNavigation();

  const { teste, enviaResultadoBancoT4,marcador,marcarAluno} = useContext(AuthContext);

  function opcao(resposta){
    if(resposta == 45){
      teste();
    }
    enviaResultadoBancoT4();
    if(marcador == false){
      
    navigation.navigate('EscolherAluno');
    }else{
      console.log("chegou no marcado!!");
      marcarAluno(false);
      navigation.navigate('Resultados_1');
    }
    
  }



 return (
  <View style={styles.container}>
  
    <Animatable.View  animation="zoomIn" style={styles.numero}>
      <Text style={styles.valor}>Qual numero esta faltando nessa sequência?</Text>

      <Text style={styles.valor1}>90 ,75 ,60 , ? ,30,15 </Text>
      
    </Animatable.View>
  
      <Animatable.View animation="zoomIn" style={styles.viewBtn}>
          <View style={styles.viewBtn1e2}>
            <TouchableOpacity style={styles.btn} >
              <Text style={styles.btnNum}>40</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btn} onPress={() => opcao(45)}>
              <Text style={styles.btnNum}>45</Text>
            </TouchableOpacity>
            
          </View>

          <View style={styles.viewBtn3e4}>
            <TouchableOpacity style={styles.btn} >
              <Text style={styles.btnNum}>25</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btn} onPress={() => navigation.navigate('Teste5')}>
              <Text style={styles.btnNum}>33</Text>
            </TouchableOpacity>
          </View>
      </Animatable.View>

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor:'#38a69d'
    },
    numero:{
      flex:2,
      backgroundColor:'#fff',
      borderRadius:20,
      margin:'5%',
      alignItems:'center',
      justifyContent:'center'
    },
    valor:{
      fontSize:30,
      fontWeight:'bold',
      textAlign:'center',
      marginBottom:10
    },
    pergunta:{
      flex:1
    },
    viewBtn:{
      flex:1,
      marginTop:'20%'
      
    },
    viewBtn1e2:{
     flexDirection:'row',
     justifyContent:'space-between',
     marginLeft:'5%',
     marginRight:'5%'
    },
    viewBtn3e4:{
     flexDirection:'row',
     marginTop:50,
     justifyContent:'space-between',
     marginLeft:'5%',
     marginRight:'5%'
    },
    btn:{
     backgroundColor:'#fff',
     width:'44%',
     height:50,
     borderRadius:10,
     marginTop:-25,
     alignItems:'center',
     justifyContent:'center'
     
    },
    txt:{
      fontSize:28,
      fontWeight:'bold',
      color:'#fff',
      marginLeft:'5%',
      marginRight:'5%',
      textAlign:'center'
    },
    btnNum:{
      fontSize:25,
      fontWeight:'bold'
    },
    valor1:{
      fontSize:35,
      fontWeight:'bold',
      textAlign:'center',
      marginTop:10
    }
})
