import React,{useState} from 'react';
import { View, Text,StyleSheet, TextInput, TouchableOpacity,Image } from 'react-native';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';

import { signInWithEmailAndPassword} from 'firebase/auth';
import { auth } from '../../../BancoNoSql/conexaoFirebase';

export default function SignIn() {
  //variaveis que recebem email e senha para fazer loguin no firebase
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const navigation = useNavigation();

  async function login(){//Faz conexao com o banco, verifica se o email esta cadastrado e faz login
    await signInWithEmailAndPassword(auth, email,password)
    .then(value => {
        alert('loguin com sucesso!');
        navigation.navigate('PainelControle');
        setEmail('');
        setPassword('');
        
    })
    .catch(error => {console.log(error); //tratamento de erro de senha e email nao cadastrado
          let strr = error;
          if(strr == "FirebaseError: Firebase: Error (auth/wrong-password)."){
          alert("senha incorreta");
           setPassword('');
          }
          if(strr == "FirebaseError: Firebase: Error (auth/user-not-found)."){
          alert("E-mail não cadastrado!")
          }
          
    });
}
 return (
   <View style={styles.container}>
       <Animatable.View animation="fadeInLeft" delay={500}
        style={styles.containerHeader}
       >
           <Text style={styles.message}>Bem-vindo(a)</Text>
       </Animatable.View >

       <Animatable.View animation="fadeInUp" style={styles.containerForm}>
           <Text style={styles.title}>E-mail</Text>
           <TextInput
            value={email}
            onChangeText={(value) => setEmail(value)}
            placeholder='Digite seu E-mail'
            style={styles.input}
           />
           <Text style={styles.title}>Senha</Text>
           <TextInput
           value={password}
           onChangeText={(value) => setPassword(value)}
            style={styles.input}
            placeholder='Digite sua senha'
           />

           <TouchableOpacity style={styles.button} onPress={() => login()}>
             <Text style={styles.buttonTxt}>Entrar</Text>
           </TouchableOpacity>

           <TouchableOpacity style={styles.googleBtn}>
           <Image
           source={require('../../../assets/google.png')}
           style={styles.imagem}
           />
           <Text style={styles.googleBtnTxt}>Entrar com Google</Text>
           </TouchableOpacity>

           <TouchableOpacity style={styles.facebookBtn}>
             <Image
             source={require('../../../assets/facebook.png')}
             style={styles.imagem2}
             />
             <Text style={styles.facebookBtnTxt}>Entrar com Facebook</Text>
           </TouchableOpacity>

           <TouchableOpacity style={styles.buttonRegister} onPress={() => navigation.navigate('SignUp')}>
             <Text style={styles.registerTxt}>Não possui uma conta? Cadastre-se</Text>
           </TouchableOpacity>
       </Animatable.View>
   </View>
  );
}

const styles = StyleSheet.create({
  container:{
  flex:1,
  backgroundColor:'#38a69d',

  },
  containerHeader:{
    marginTop:'14%',
    marginBottom:'8%',
    paddingStart:"5%",
  },
  message:{
    fontSize:28,
    fontWeight:'bold',
    color:'#fff'
  },
  containerForm:{
    flex:1,
    backgroundColor:'#fff',
    borderTopLeftRadius:25,
    borderTopRightRadius:25,
    paddingStart:'5%',
    paddingEnd:'5%'
  },
  title:{
    fontSize:20,
    marginTop:28,

  },
  input:{
    borderBottomWidth:1,
    height:40,
    marginBottom:12,
    fontSize:16,
  },
  button:{
    backgroundColor:'#38a69d',
    width:'100%',
    borderRadius:5,
    paddingVertical: 8,
    marginTop:14,
    justifyContent:'center',
    alignItems:'center'
  },
  buttonTxt:{
    color:'#fff',
    fontSize:18,
    fontWeight:'bold'
  },
  buttonRegister:{
    marginTop:14,
    alignSelf:'center'
  },
  registerTxt:{
    color:'#a1a1a1'
  },
  imagem:{
    width:50,
    height:50
  },
  imagem2:{
    width:38,
    height:38,
    marginLeft: 7,
    marginTop:4
  },
  googleBtn:{
   flexDirection:'row',
   borderWidth:1,
   marginTop:15,
   borderRadius:5,
   width:'70%',
   alignSelf:'center'

  },
  googleBtnTxt:{
    alignSelf:'center',
    fontSize:16
  },
  facebookBtn:{
    flexDirection:'row',
    borderWidth:1,
    marginTop:15,
    borderRadius:5,
    width:'70%',
    alignSelf:'center',
    height:50
  },
  facebookBtnTxt:{
    alignSelf:'center',
    fontSize:16,
    marginLeft: 5
  }

})
