        console.log(userId);
          if(avaliarGb == true)  {
              
        const myDoc = ref(db,userId);
         
        onValue(myDoc, (snapshot) => {
            
            const data = snapshot.val();
            let lt = [];
          
            lt.push(data);
             
             dd = lt.map(function(obj){
              return Object.keys(obj).map(function(key) { //converte em array
    
                return obj[key];
              })
            })

              let b = dd[0].map((item) => {
                return item;
              })

              let g =lt.map((item) => {
                  return item;
              })

              let cont = b.length;
              setNomeRecebido(b);
              setContador(cont);
              console.log("b: ",b);
              console.log("g: ",g);
              

        })

        setAvaliarGp(false);
    }
          
    },[]);
 return (
   <View style={styles.container}>
      < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Avaliar Grupo</Text>
       </Animatable.View>

       <Text style={styles.txtEscolha}>Escolha um Grupo para Avaliar</Text>

       <View style={styles.viewFlatList}>
           <FlatList/>
       </View>

       <View style={styles.viewBtn}>
           <TouchableOpacity style={styles.btnIniciar} onPress={() => navigation.navigate('EscolherAluno')}>
             <Text style={styles.btnIniciarTxt}>Iniciar AvaliaÃ§Ã£o</Text>
           </TouchableOpacity>
       </View>
       

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#38a69d',
       },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
