import React,{useContext,useState,useEffect} from 'react';
import { View,Text,StyleSheet, TouchableOpacity } from 'react-native';

import * as Animatable from 'react-native-animatable';
import { useNavigation } from '@react-navigation/native';

import { AuthContext } from '../../../../contexts/auth'


export default function Teste1_2({route}) {
  //Vetor estático
  const [questao, setQuestao] = useState([130,160,210,300,175,30,50,80,240,35]);

  //Variavel que aponta para um index do vetor "questao"
  const [x, setX] = useState(questao[Math.floor(Math.random() * 10)]);

  //Vetor que recebe  "x" somado com outros valores
  const [num, setNum] = useState([]);

  // "x" anterior
  const [xResp1, setxResp1] = useState(route.params?.numeroResp1);

  const { teste} = useContext(AuthContext);

  const navigation = useNavigation();

  useEffect(() => {
    
    if(x === xResp1){ //verificando se o "x" da pergunta anterior é igual a esta, se for, gera outro numero pseudo aleatorio
        setX(questao[Math.floor(Math.random() * 10)])
    }
     //populando vetor "Num"
    setNum([x + 50, x + 100, x + 35, x + 30, x +150, x + 150, x + 200, x + 110, x - 25, x -25])
  },[]);

  function opcao(resposta){
    //função chama o próximo teste, envia a variavel 'x' para que o proximo numero de comparação nao seja igual
    //ao anterior e envia a responta para o banco de dados "Firebase".
    if(resposta == num[9]){
      teste();
    }
    

    navigation.navigate('Teste1_3', {
        numeroResp1:xResp1,
        numeroResp2:x
      })
  }
 
  
 return (
   <View style={styles.container}>

    <Animatable.View  animation="zoomIn" style={styles.numero}>
      <Text style={styles.valor}>{x}</Text>
    </Animatable.View>

     <Animatable.View animation="fadeInLeft" style={styles.pergunta}>
       <Text style={styles.txt}>Qual numero abaixo esta mais proximo de {x}?</Text>
     </Animatable.View>
      
      <Animatable.View animation="zoomIn" style={styles.viewBtn}>
          <View style={styles.viewBtn1e2}>
            <TouchableOpacity style={styles.btn} >
              <Text style={styles.btnNum}>{num[4]}</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btn}>
              <Text style={styles.btnNum}>{num[1]}</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.viewBtn3e4}>
            <TouchableOpacity style={styles.btn} onPress={() => opcao(num[9])}>
              <Text style={styles.btnNum}>{num[9]} </Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btn}>
              <Text style={styles.btnNum}>{num[3]}</Text>
            </TouchableOpacity>
          </View>
      </Animatable.View>

   </View>
  );
}

//Estilização da pagina
const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor:'#38a69d'
    },
    numero:{
      flex:2,
      backgroundColor:'#fff',
      borderRadius:170,
      margin:'10%',
      alignItems:'center',
      justifyContent:'center'
    },
    valor:{
      fontSize:75,
      fontWeight:'bold',
    },
    pergunta:{
      flex:1
    },
    viewBtn:{
      flex:1,
      
    },
    viewBtn1e2:{
     flexDirection:'row',
     justifyContent:'space-between',
     marginLeft:'5%',
     marginRight:'5%'
    },
    viewBtn3e4:{
     flexDirection:'row',
     marginTop:50,
     justifyContent:'space-between',
     marginLeft:'5%',
     marginRight:'5%'
    },
    btn:{
     backgroundColor:'#fff',
     width:'44%',
     height:50,
     borderRadius:10,
     marginTop:-25,
     alignItems:'center',
     justifyContent:'center'
     
    },
    txt:{
      fontSize:28,
      fontWeight:'bold',
      color:'#fff',
      marginLeft:'5%',
      marginRight:'5%'
    },
    btnNum:{
      fontSize:25,
      fontWeight:'bold'
    }
})
