import React,{useState} from 'react';
import { View, Text,StyleSheet, TextInput, TouchableOpacity } from 'react-native';

import * as Animatable from 'react-native-animatable';
import { useNavigation } from '@react-navigation/native';

import {createUserWithEmailAndPassword} from 'firebase/auth';
import { auth } from '../../../BancoNoSql/conexaoFirebase';


export default function SignUp() {
  const [email, setEmail] = useState('');
  const [ password, setPassword] = useState('');
  const [ passwordConf, setPasswordConf] = useState('');

  const navigation = useNavigation();

  function erro (er){
    if(er == er){
       alert("Esse E-mail ja existe ");
    }
   }

   async function CriarConta(){
     if(password == passwordConf){
       await createUserWithEmailAndPassword(auth, email,password)
       .then(value => {
           alert('cadastrado com sucesso');
           console.log("uid: "+ value.user.uid);
       })
       .catch(error => {
           console.log(error);
           erro(error)}
           );
       
      setEmail('');
      setPassword('');
      setPasswordConf('');
   }else{
     alert("As senhas devem ser iguais");
     setPassword('');
     setPasswordConf('');
   }
  }
 return (
   <View style={styles.container}>
       <Animatable.View animation="fadeInLeft" delay={500}
        style={styles.containerHeader}
       >
           <Text style={styles.message}>Cadatro</Text>
       </Animatable.View >

       <Animatable.View animation="fadeInUp" style={styles.containerForm}>
           <Text style={styles.title}>E-mail</Text>
           <TextInput
            placeholder='Digite seu E-mail'
            value={email}
            onChangeText={(value) => setEmail(value)}
            style={styles.input}
           />
           <Text style={styles.title}>Senha</Text>
           <TextInput
            value={password}
            onChangeText={(value) => setPassword(value)}
            style={styles.input}
            placeholder='Digite sua senha'
           />
           <Text style={styles.title}>Repetir Senha</Text>
           <TextInput
            value={passwordConf}
            onChangeText={(value) => setPasswordConf(value)}
            style={styles.input}
            placeholder='Digite sua senha'
           />

           <TouchableOpacity style={styles.button}
            onPress={() => CriarConta()}
           >
             <Text style={styles.buttonTxt}>Cadastrar</Text>
           </TouchableOpacity>

           <TouchableOpacity style={styles.buttonRegister} onPress={() => navigation.navigate('SignIn')}>
             <Text style={styles.registerTxt}>Ja possui uma conta? Entrar</Text>
           </TouchableOpacity>
       </Animatable.View>
   </View>
  );
}

const styles = StyleSheet.create({
  container:{
  flex:1,
  backgroundColor:'#38a69d',

  },
  containerHeader:{
    marginTop:'14%',
    marginBottom:'8%',
    paddingStart:"5%",
  },
  message:{
    fontSize:28,
    fontWeight:'bold',
    color:'#fff'
  },
  containerForm:{
    flex:1,
    backgroundColor:'#fff',
    borderTopLeftRadius:25,
    borderTopRightRadius:25,
    paddingStart:'5%',
    paddingEnd:'5%'
  },
  title:{
    fontSize:20,
    marginTop:28,

  },
  input:{
    borderBottomWidth:1,
    height:40,
    marginBottom:12,
    fontSize:16,
  },
  button:{
    backgroundColor:'#38a69d',
    width:'100%',
    borderRadius:5,
    paddingVertical: 8,
    marginTop:14,
    justifyContent:'center',
    alignItems:'center'
  },
  buttonTxt:{
    color:'#fff',
    fontSize:18,
    fontWeight:'bold'
  },
  buttonRegister:{
    marginTop:14,
    alignSelf:'center'
  },
  registerTxt:{
    color:'#a1a1a1'
  }
})
