import React,{useContext,useEffect,useState} from 'react';
import { View,Text, TouchableOpacity,FlatList, StyleSheet} from 'react-native';
import { Checkbox } from 'react-native-paper';
import * as Animatable from 'react-native-animatable';

import {  useNavigation } from '@react-navigation/native';
import { AuthContext } from '../../../contexts/auth';
import { db } from '../../../BancoNoSql/conexaoFirebase';
import { ref, set, onValue } from 'firebase/database';



export default function AvaliarGrupo({route}) {

    const [selecionado, setSelecionado] = useState(false);
    const [modAlunos, setModAlunos] = useState(false);
    const [checked1, setChecked1] = useState(false);

    const navigation = useNavigation();
    const { user , avaliartruefalse, AvaliarGp,guardaGrupo} = useContext(AuthContext);

    //const [avaliarGb, setAvaliarGp] = useState(route.params.liberaGP);

    const [userId, setUserId]=useState(user.userId+'/');
    const [contador,setContador] = useState(0);

    const [nomeRecebido, setNomeRecebido]=useState([]);

    var dd =[];
    useEffect(() => {

        console.log(userId);

         if(AvaliarGp == true)  {
              
        const myDoc = ref(db,userId+"nomesDeGrupos/");
         
        onValue(myDoc, (snapshot) => {
            
            const data = snapshot.val();
            let lt = [];
          
            lt.push(data);
             
             dd = lt.map(function(obj){
             return Object.keys(obj).map(function(key) { //converte em array
    
               return obj[key];
             })
           })

             let g = dd[0].map((item) => {
               return item;
             })

              //let g =lt.map((item) => {
              //    return item.nomesDeGrupos;
             // })

              let cont = g.length;
              setNomeRecebido(g);
              setContador(cont);
              //console.log("b: ",b);
              console.log("g: ",g);
              

        })

        avaliartruefalse(false);
    }
          
    },[]);

    function modalAlunos(val){
     
      let nomeGBavaliar = val;
      guardaGrupo(val);
      navigation.navigate("EscolherAluno");

    }
 return (
   <View style={styles.container}>
      < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Avaliar Grupo</Text>
       </Animatable.View>

       <Text style={styles.txtEscolha}>Escolha um Grupo para Avaliar</Text>

       <View style={styles.viewFlatList}>
           <FlatList
            data={nomeRecebido}
            renderItem={ ({item}) => 
                        <View style={styles.viewFlat}>
                            
                            <TouchableOpacity style={styles.btnFlat} onPress={() => modalAlunos(item.nomeg)}>
                            
                            <Text style={styles.txtFlat}>{item.nomeg}</Text>
                            </TouchableOpacity>
                        </View>
                        }
            keyExtractor={(item) => item.nomeg}
           />
       </View>

      {/* <View style={styles.viewBtn}>
           <TouchableOpacity style={styles.btnIniciar} onPress={() => navigation.navigate('EscolherAluno')}>
             <Text style={styles.btnIniciarTxt}>Iniciar Avalia√ß√£o</Text>
           </TouchableOpacity>
                    </View>*/}
       

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#38a69d',
       },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewFlatList:{
        flex:4,
        margin:'5%',
        backgroundColor:'#fff',
        borderRadius:10
    },
    viewBtn:{
        flex:1,
        alignItems:'center'
    },
    btnIniciar:{
        backgroundColor:'#fff',
        alignItems:'center',
        justifyContent:'center',
        width:'90%',
        height:40,
        borderRadius:10
    },
    btnIniciarTxt:{
        fontSize:18,
        fontWeight:'bold',
        color:'#38a69d'
    },
    txtEscolha:{
        textAlign:'center',
        fontSize:16,
        fontWeight:'bold',
        color:'#fff',
        marginBottom:-10
    },
    viewFlat:{
       justifyContent:'center',
       alignItems:'center'
    },
    txtFlat:{
        
        fontSize: 16,
        fontWeight:'bold'
    },
    btnFlat:{
        borderWidth:1,
        width: "85%",
        height: 30,
        alignItems:'center',
        justifyContent:'center',
        marginTop:15,
        borderRadius:15
    }
})
