import React from 'react';
import { View,Text, TouchableOpacity,FlatList, StyleSheet } from 'react-native';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';

export default function Desafios() {

    const navigation = useNavigation();
 return (
   <View style={styles.container}>
      < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Desafios</Text>
       </Animatable.View>

       <Text style={styles.txtEscolha}>Escolha um grupo para aplicar desafios</Text>

       <Animatable.View animation="zoomIn" style={styles.viewFlatList}>
           <FlatList/>
       </Animatable.View>

       <Animatable.View animation="zoomIn" style={styles.viewBtn}>
           <TouchableOpacity style={styles.btnIniciar}onPress={() => navigation.navigate('Desafio1')}>
             <Text style={styles.btnIniciarTxt}>Avançar</Text>
           </TouchableOpacity>
       </Animatable.View>
      

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#38a69d',
       },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewFlatList:{
        flex:4,
        margin:'5%',
        backgroundColor:'#fff',
        borderRadius:10
    },
    viewBtn:{
        flex:1,
        alignItems:'center',
        marginBottom:-25
    },
    viewBtn2:{
        flex:1,
        alignItems:'center',
        
    },
    btnIniciar:{
        backgroundColor:'#fff',
        alignItems:'center',
        justifyContent:'center',
        width:'90%',
        height:40,
        borderRadius:10
    },
    btnIniciarTxt:{
        fontSize:18,
        fontWeight:'bold',
        color:'#38a69d'
    },
    txtEscolha:{
        textAlign:'center',
        fontSize:14,
        fontWeight:'bold',
        color:'#fff',
        marginBottom:-10
    }
})
