        break; 
     
}
  
  if(jogador1 == true){

    switch (n) {
        case 1: setSty(true);   
            break; 
        case 2: setSty1(true);   
            break; 
        case 3: setSty2(true);   
            break; 
        case 4: setSty3(true);   
            break; 
