        case 11: setSty10(true);   
            break; 
        case 12: setSty11(true);   
            break; 
        
      
    }

  }

  setContador(contador + 1);

  if((contador == jogadas)&&(jogador1 == true) ){
     setJogador1(false);
     setJogador2(true);
     setContador(1);
  }else if ((contador == jogadas) && (jogador2 == true)){
    setJogador2(false);
    setJogador1(true);
    setContador(1);
  }

}

