import React,{useContext,useState,useEffect} from 'react';
import { View, Text, StyleSheet,TextInput, TouchableOpacity, FlatList } from 'react-native';

import {AlunoModel} from '../AlunoModel';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';

import { AuthContext } from '../../../contexts/auth'

import { db } from '../../../BancoNoSql/conexaoFirebase';
import { ref, set, onValue } from 'firebase/database';

export default function InserirAluno({route}) {
    var cont =0;
    const navigation = useNavigation();
    const { user } = useContext(AuthContext);

    const [userId, setUserId]=useState(user.userId+'/');

    const [nomeGrupo, setNomeGrupo] = useState(route.params.nomeGrupo+'/');

    const [contador,setContador] = useState(0);
    const [aluno,setAluno] = useState('aluno');

    const [nome,setNome] = useState('');
    const [idade,setIdade] = useState('');

    const [nomeRecebido, setNomeRecebido]=useState('');

    const [dataAlunos, setDataAlunos] = useState([]);

    const dataFire =[
        { nome: "hugo", idade:'2' },
        { nome: "kathelly", idade:'3' }];

        useEffect(() => {
            
         const myDoc = ref(db,userId + nomeGrupo);
                
        
                onValue(myDoc, (snapshot) => {
                    const data = snapshot.val();
                    
                    setDataAlunos(data);
                   // setNomeRecebido(data.nome);
                    let dat =[];
                    
                    dataAlunos.forEach((item) => {
                     dat.push(item.nome);
                    })
                    console.log("data: "+dataAlunos);
                    console.log("dat: "+dat);
                })
        
            
            
          
        },[]);


    function setarAlunoEmGrupo(){
        setContador(contador+1);
        console.log("contador: "+contador);
        cont = contador;
        let alunoVal = (aluno+cont+'/');
        console.log(alunoVal);
        set(ref(db, userId + nomeGrupo + alunoVal ), {
            nome: nome,
            idade: idade

        }).then(() => {
            alert("deu certo!")
            
            })
          .catch((error) => {
            alert(error.message)
            alert("falhou");
            })
    }

    const Read = () => {
        const myDoc = ref(db,userId + nomeGrupo);
        

        onValue(myDoc, (snapshot) => {
            const data = snapshot.val();
   
            setNomeRecebido(data.nome);
            let valor = data;
            let al ='aluno0/'
            console.log("estado: "+nomeRecebido);
            console.log("let: "+valor.aluno0.nome);
        })

       
    }

  

 return (
   <View style={styles.container}>
       < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Criar Grupo</Text>
       </Animatable.View>

       <View style={styles.viewInput}>
           <TextInput
           placeholder='Digite o nome do Aluno'
           style={styles.input}
           value={nome}
           onChangeText={(value) => setNome(value)}
           />

       </View>
       <View style={styles.viewInput2}>
            <TextInput
                placeholder='Idade'
                style={styles.input2}
                value={idade}
                onChangeText={(value) => setIdade(value)}
                />

            <TouchableOpacity style={styles.btnAdd}
             onPress={() => Read()}
            >
                <Text style={styles.btnAddTxt}>Adicionar</Text>
            </TouchableOpacity>
       </View>

       <Text style={styles.alunosCad}>Alunos Cadastrados</Text>
        
       < Animatable.View animation="zoomIn" style={styles.viewRodape}>
         <View>{/*é preciso otimizar esse flatlist*/}
             <View style={{flexDirection:'row'}}>
             <Text style={{marginLeft:"3%", fontSize:18, fontWeight:'bold',width:"70%",borderBottomWidth:1}}>Nome</Text>
             <Text style={{marginLeft:"3%", fontSize:18, fontWeight:'bold',width:"20%",borderBottomWidth:1}}>Idade</Text>
             </View>
             <FlatList
                data={dataFire}
                renderItem={ ({item}) => 
                            <View style={{flexDirection:'row', justifyContent:'space-between'}}>
                            <Text style={{marginLeft:'3%',marginTop:5, fontSize: 16}}>{item.nome}</Text>
                            <Text style={{marginRight:'14%',marginTop:5, fontSize: 17, fontWeight:'bold'}}>{item.idade}</Text>
                            </View>}
                keyExtractor={(item) => item.nome}
             />
         </View>
        
       </Animatable.View>

       <Animatable.View animation="lightSpeedIn" style={styles.viewBtnFinalizar}>
             <TouchableOpacity style={styles.btnFinalizar} onPress={() => navigation.navigate('PainelControle')}>
                 <Text style={styles.btnFinalizarTxt}>Finalizar</Text>
             </TouchableOpacity>
         </Animatable.View>
        

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
     flex:1,
     backgroundColor:'#38a69d',
    },
    viewInput:{
        marginTop:'-20%',
        alignItems:'center',
    },
    viewInput2:{
        
        marginLeft:'13%',
        marginTop:10,
        flexDirection:'row'
    },
    input:{
        borderBottomWidth:1,
        borderBottomColor:'#fff',
        width:'75%',
        color:'#fff',
        fontSize:16,
        fontWeight:'bold'
    },
    input2:{
        borderBottomWidth:1,
        borderBottomColor:'#fff',
        width:'20%' ,
        marginBottom:5,
        color:'#fff',
        fontSize:16,
        fontWeight:'bold'
    },
    btnAdd:{
        backgroundColor:'#fff',
        width:'50%',
        height: 40,
        borderRadius:10,
        alignItems:'center',
        justifyContent:'center',
        marginLeft:50,
        marginTop:10
    },
    btnAddTxt:{
        fontSize:18,
        color:'#38a69d',
        fontWeight:'bold'
    },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewRodape:{
        flex:2,
        backgroundColor:'#fff',
        borderRadius:10,
        margin:'5%'
    },
    viewBtnFinalizar:{
        marginTop:5,
        marginBottom:'10%',
        alignItems:'center'
    },
    btnFinalizar:{
        backgroundColor:'#fff',
        width:'75%',
        height:40,
        borderRadius:10,
        alignItems:'center',
        justifyContent:'center'
    },
    btnFinalizarTxt:{
        fontSize:18,
        fontWeight:'bold',
        color:'#38a69d'
    },
    alunosCad:{
        textAlign:'center',
        marginTop:'5%',
        marginBottom:-15,
        fontSize:16,
        color:'#fff',
        fontWeight:'bold'
    }
})
