import React, {useState} from 'react';
import { View,Text, TouchableOpacity,FlatList, StyleSheet,Modal, Image } from 'react-native';
import AppIntroSlider from 'react-native-app-intro-slider';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';




export default function EscolherAluno() {

    const slider = [
        {
          key:'1',
          title:'',
          text:'',
          image: <Image
          source={require('../../../assets/tuto1_1.png')}
          style={{
              resizeMode:'center',
              height:'70%',
              width:'80%',
              borderWidth:1,
              borderColor:'#fff',
              marginTop:-20
          }}
         /> 
        },
        {
            key:'2',
            title:'Exemplo de exercício',
            text:'Basta pressionar o número que você acha mais próximo de 10',
            image:   <Image
            source={require('../../../assets/tuto1_2.png')}
            style={{
                resizeMode:'center',
                height:'70%',
                width:'80%',
                borderWidth:1,
                borderColor:'#fff',
                marginTop:-25
            }}
           />  
          },
          {
            key:'3',
            title1:'Entendido? Vamos começar!',
            text2:'Basta pressionar o número que corresponde a quantidade de bolinhas da tela anterior',
            butto: <View style={{flex:1,justifyContent:'center', marginBottom:'20%'}}>
                <TouchableOpacity onPress={() => chamaTeste2()} style={{borderRadius:150,backgroundColor:'#fff',width:250,height:250, justifyContent:'center'}}>
                <Text style={{fontSize:50,fontWeight:'bold',textAlign:'center',color:'#38a69d'}}>Iniciar</Text>
            </TouchableOpacity></View>
            
          }
    ]

    const [visivel, setVisivel] = useState(false);

    const navigation = useNavigation();

    function chamaModal(){
        setVisivel(true);
    }

    function chamaTeste2(){
        setVisivel(false)
        navigation.navigate('Teste1_1')
    }

    function renderSliders({item}){
        return(
     <View style={{flex:1,alignItems:'center'}}>
        <Text style={{fontSize:28, color:'#fff', textAlign:'center',marginTop:15, fontWeight:'bold'}}>{item.title}</Text>
        <Text style={{fontSize:26, color:'#fff', textAlign:'center',marginTop:15, fontWeight:'bold'}}>{item.title1}</Text>

       {item.image}
       {item.butto}
       <Text style={{fontSize:20, color:'#fff', textAlign:'center', marginTop:15}}>{item.text}</Text>
     </View>
        )
    }
 return (
   <View style={styles.container}>
      < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Avaliar Grupo</Text>
       </Animatable.View>

       <Text style={styles.txtEscolha}>Escolha um aluno para avaliar ou avalie todo o grupo</Text>

       <Animatable.View animation="zoomIn" style={styles.viewFlatList}>
           <FlatList/>
       </Animatable.View>

       <Animatable.View animation="zoomIn" style={styles.viewBtn}>
           <TouchableOpacity style={styles.btnIniciar}onPress={() => chamaModal()}>
             <Text style={styles.btnIniciarTxt}>Avaliar Aluno</Text>
           </TouchableOpacity>
       </Animatable.View>
       <Animatable.View animation="zoomIn" style={styles.viewBtn2}>
           <TouchableOpacity style={styles.btnIniciar}>
             <Text style={styles.btnIniciarTxt}>Avaliar Grupo</Text>
           </TouchableOpacity>
       </Animatable.View>

       {/*Modal que exibe um tutorial simples do teste a seguir*/}
       <Modal animationType='slide' visible={visivel}>
           <View style={styles.container}>
              <AppIntroSlider
               renderItem={renderSliders}
               data={slider}
               activeDotStyle={{
                   backgroundColor:'#fff',
                   width:30
               }}
               renderNextButton={() => {}}
               renderDoneButton={() => {}}
               
              />

              
           </View>
       </Modal>

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#38a69d',
       },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewFlatList:{
        flex:4,
        margin:'5%',
        backgroundColor:'#fff',
        borderRadius:10
    },
    viewBtn:{
        flex:1,
        alignItems:'center',
        marginBottom:-25
    },
    viewBtn2:{
        flex:1,
        alignItems:'center',
        
    },
    btnIniciar:{
        backgroundColor:'#fff',
        alignItems:'center',
        justifyContent:'center',
        width:'90%',
        height:40,
        borderRadius:10
    },
    btnIniciarTxt:{
        fontSize:18,
        fontWeight:'bold',
        color:'#38a69d'
    },
    txtEscolha:{
        textAlign:'center',
        fontSize:14,
        fontWeight:'bold',
        color:'#fff',
        marginBottom:-10
    }
})
