import React,{useContext,useEffect,useState} from 'react';
import { View,Text, TouchableOpacity,FlatList, StyleSheet,Modal, Image, Alert, ScrollView } from 'react-native';
import AppIntroSlider from 'react-native-app-intro-slider';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';

import { AuthContext } from '../../../../contexts/auth';
import { db } from '../../../../BancoNoSql/conexaoFirebase';
import { ref, set, onValue } from 'firebase/database';

import {Picker} from '@react-native-picker/picker';

let inser = true;




export default function Resultados_1({route}) {

    const { user, guardaVariaveisCorrent,nomeGBavaliar, aplicaTesteFalta, marcador, marcarAluno,contTeste} = useContext(AuthContext);

    const [userId, setUserId]=useState(user.userId+'/');
    const [nomeGrupo, setNomeGrupo] = useState(nomeGBavaliar+'/');
    const [nomeRecebido, setNomeRecebido]=useState([]);
    const [visivel, setVisivel] = useState(false);

    const navigation = useNavigation();
    
    //variaveis do modal
    const [modalNome, setModalNome] = useState('');
    const [modalIdade, setModalIdade] = useState('');
    const [modalTeste1, setModalTeste1] = useState(0);
    const [modalTeste2, setModalTeste2] = useState(0);
    const [modalTeste3, setModalTeste3] = useState(0);
    const [modalTeste4, setModalTeste4] = useState(0);

    const [modalMedia, setModalMedia] = useState(0);
    const [modalFalta, setModalFalta] = useState(0);
    const [modalAval, setModalAval] = useState(0);

    const [modalAlunoRef, setModalAlunoRef] = useState([]);

    const [modalResultado, setModalResultado] = useState(false);
    const [modalResultadoGrupo, setModalResultadoGrupo] = useState(false);

    const [avaliado , setAvaliado ] = useState();
    const [avaliado1 , setAvaliado1 ] = useState(false);
    const [avaliado2 , setAvaliado2 ] = useState(false);

    const [alunoRef , setAlunoRef ] = useState('');

    const [gruposPicker1,setGruposPicker1] = useState('');
    const [gruposPicker2,setGruposPicker2] = useState();

    let testePicker ='javascript';
    let testePicker1 ='python';
    let testePicker2 ='c#';

    var dd =[];
    let b =0;
    let falta=0;
    let d =0;
    let test =[];
    let teste1 = 'teste1/';
    let teste2 = 'teste2/';
    let teste3 = 'teste3/';

    useEffect(() => {
      FlatRefresh();
     
    },[])

    function aplicaTeste(alunoReferencia, avaliado){//vai direto de Resultados_1 para o primeiro teste levando o aluno escolhido
        if(avaliado == false){  
            guardaVariaveisCorrent(alunoReferencia, nomeGrupo);
            aplicaTesteFalta(true);
            marcarAluno(true);
            contTeste(teste1);
            navigation.navigate("EscolherAluno")
                }
                else {
                    alert("Este aluno ja foi avaliado!");
                }
    }

    function aplicarTesteNovo(avaliado, avaliado1, avaliado2,alunoReferencia){
        if((avaliado == true)&&(avaliado1 == false)){
           guardaVariaveisCorrent(alunoReferencia, nomeGrupo);
           aplicaTesteFalta(true);
           marcarAluno(true);
           contTeste(teste2);
           navigation.navigate("EscolherAluno")
   
        }else if((avaliado == true)&&(avaliado1 == true)&&(avaliado2 == false)){
           guardaVariaveisCorrent(alunoReferencia, nomeGrupo);
           aplicaTesteFalta(true);
           marcarAluno(true);
           contTeste(teste3);
           navigation.navigate("EscolherAluno")
        }else{
            alert("Numero maximo de testes atingido!");
        }
       }

    function modalGrupo(){
     setModalResultadoGrupo(true);
     calculaMediaGrupo();

    }
    function calculaMediaGrupo(){
        
        let a = modalAlunoRef.forEach((item) => {
            return (item.avaliado == true )? (b = b + ((item.estimativa + item.logica + item.memoria + item.visaoEspacial)/4)) && d++ :falta++
        })
         setModalMedia(b/d);
         setModalFalta(falta);
         // console.log("Media do grupo: "+(b/d));
         // console.log("qtd alunos avaliados: "+d);
         // console.log("falta avaliar "+falta+" alunos");
        }

  


    function chamaModal(alunoRef,avaliado,nome,idade,avaliado1,avaliado2){
        if(avaliado == true){
          setModalNome(nome);
          setModalIdade(idade);
          setAvaliado(avaliado);
          setAvaliado1(avaliado1);
          setAvaliado2(avaliado2);
          setAlunoRef(alunoRef);

          setModalResultado(true);
          buscarAluno(alunoRef);
        }else if (avaliado == false){
            Alert.alert("Aluno não avaliado!", "Deseja avaliar aluno(a): "+nome+" agora ?",[
                {
                    text: "Sim",
                    onPress: () => aplicaTeste(alunoRef,avaliado),
                },
                {
                    text: "Não",
                    
                }
            ])
        }
        
    }
    function chamaModalGrupo(alunoRef,avaliado,nome,idade,teste1,teste2,teste3,teste4){
      console.log(teste1);
    }

    function FlatRefresh(){ //atualiza o array do flatlist "alunos"
         const myDoc = ref(db,userId + nomeGrupo );
   
         onValue(myDoc, (snapshot) => {
             
             const data = snapshot.val();
             let lt = [];
           
             lt.push(data);
             
              dd = lt.map(function(obj){
               return Object.keys(obj).map(function(key) { //converte em array
     
                 return obj[key];
               })
             })
         
     
               let b = dd[0].map((item) => {
                 return item;
               })
 
               let cont = b.length;
               setNomeRecebido(b);//array do flat list 
         })
    }

    function buscarAluno(alunoReferencia){
        const myDoc = ref(db,userId + nomeGrupo +alunoReferencia+'/');
         let al = [];
        onValue(myDoc, (snapshot) => {
            
            const data = snapshot.val();
            let lt = [];
          
            lt.push(data);
            
             al = lt.map(function(obj){
              return Object.keys(obj).map(function(key) { //converte em array
    
                return obj[key];
              })
            })
        
    
              let b = al[0].map((item) => {
                return item;
              })
              console.log(b);
              let cont = b.length;
              setModalAlunoRef(b);//array do flat list 
              test.push(b);
        })
    }

 
 return (
   <View style={styles.container}>
      < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           
           <Text style={styles.title}>{nomeGBavaliar}</Text>
       </Animatable.View>

       <Text style={styles.txtEscolha}>Escolha um aluno para ver o resultado </Text>

       <Animatable.View animation="zoomIn" style={styles.viewFlatList}>
           <FlatList
           data={nomeRecebido}
           renderItem={ ({item}) => 
           <View style={styles.viewFlat}>
               
               <TouchableOpacity style={(item.avaliado == true)? styles.btnFlatAvaliado : styles.btnFlat}
                onPress={() => chamaModal(
                item.alunoReferencia,item.avaliado,item.nome,item.idade,item.avaliado1, item.avaliado2
                )}>
               
               <Text style={styles.txtFlat}>{item.nome}</Text>
               </TouchableOpacity>
           </View>
           }
          keyExtractor={(item) => item.nome}
           
           />
       </Animatable.View>

       <Animatable.View animation="zoomIn" style={styles.viewBtn2}>
           <TouchableOpacity style={styles.btnIniciar} onPress={() => modalGrupo()}>
             <Text style={styles.btnIniciarTxt}>Informações do grupo</Text>
           </TouchableOpacity>
        </Animatable.View> 

      <Animatable.View animation="zoomIn" style={styles.viewBtn}>
           <TouchableOpacity style={styles.btnIniciar} onPress={() => navigation.navigate("InserirAluno", {inserirTrue : inser})}>
             <Text style={styles.btnIniciarTxt}>Editar Grupo</Text>
           </TouchableOpacity>
       </Animatable.View>

       <Animatable.View animation="zoomIn" style={styles.viewBtn2}>
           <TouchableOpacity style={styles.btnIniciar} onPress={() => navigation.navigate("PainelControle")}>
             <Text style={styles.btnIniciarTxt}>Inicio</Text>
           </TouchableOpacity>
        </Animatable.View> 
   
        <Modal animationType='fadeIn' visible={modalResultado} transparent={true}>
          
            <View style={{backgroundColor:'#38a69d',width:'80%',height:'48%', marginLeft:'10%', marginTop:'42%', borderRadius:10}}> 
                <View style={{flexDirection:'row',justifyContent:'space-between'}}> 
                    <View >
                        <Text style={styles.modalTxt}>Aluno(a): {modalNome}</Text>
                        <Text style={styles.modalTxt}>Idade: {modalIdade}</Text>
                    </View>
                    <View style={{width:40,height:40,marginBottom:15}}>
                        <TouchableOpacity style={{alignItems:'center', justifyContent:'center', width:40,height:40,backgroundColor:'red',borderRadius:10, marginTop:-5}} 
                        onPress={() => setModalResultado(false)}
                        >
                            <Text style={{color:'#fff', fontSize:25, fontWeight:'bold'}}>X</Text>
                        </TouchableOpacity>
                    </View>
                </View>

                <View style={{alignItems:'center',height:'70%'}}>
                        <FlatList
                        data={modalAlunoRef}
                        renderItem={({item}) => item.estimativa != null ?
                        
                            <View style={{flexDirection:'row',borderBottomWidth:1, borderBottomColor:"#fff"}}>
                                <View style={{ marginTop:'10%'}}>
                                
                                
                                    <Text style={{fontSize:20, color:'#fff', fontWeight:'bold', marginBottom:10}}>{item.numteste} :</Text>
                
                                    <Text style={styles.modalresult}> Estimativa          |   {item.estimativa}</Text>
                                    <Text style={styles.modalresult}> Lógica                 |   {item.logica}</Text>
                                    <Text style={styles.modalresult}> Memória             |   {item.memoria}</Text>
                                    <Text style={styles.modalresult}> Visão Espacial   |   {item.visaoEspacial}</Text>
                                
                                    <Text style={styles.modalMedia}>Média:  {parseFloat((item.estimativa + item.logica + item.memoria + item.visaoEspacial)/ 4)}</Text>
                                
                                </View>
                        
                        </View> : <Text style={{marginTop:-50}}></Text>
                        }
                        keyExtractor={(item) => item.nome}
                        showsVerticalScrollIndicator={false}
                        />
                                <View style={{marginBottom:-25, width:'80%',alignItems:'center'}}>
                                    <TouchableOpacity style={{width:'80%', height:40, backgroundColor:"#fff", borderRadius:10, alignItems:'center',justifyContent:'center'}}
                                     onPress={() => aplicarTesteNovo(avaliado,avaliado1,avaliado2,alunoRef)}
                                    >
                                        <Text style={{fontSize:17, fontWeight:'bold',color:'#38a69d'}}>Avaliar novamente</Text>
                                    </TouchableOpacity>
                                    </View>
                </View>
  
            </View>
        </Modal>

        <Modal animationType='fadeIn' visible={modalResultadoGrupo} transparent={true}>
            <View style={{borderRadius:10,width:'90%', height:'92%', margin:'5%',backgroundColor:'#38a69d', marginTop:'10%',flexDirection:'row', borderWidth:1, borderColor:'#fff'}}>
               
              <View style={{marginLeft:'3%', marginTop:'7%'}}>
                        <View style={{width:40,height:40, marginLeft:'69%', paddingLeft:'7%',marginTop:-25}}>
                            <TouchableOpacity style={{alignItems:'center', justifyContent:'center', width:40,height:40,backgroundColor:'red',borderRadius:10}} 
                            onPress={() => setModalResultadoGrupo(false)}
                            >
                                <Text style={{color:'#fff', fontSize:25, fontWeight:'bold'}}>X</Text>
                            </TouchableOpacity>
                        </View>
                        <View>
                            <Picker
                             selectedValue={gruposPicker1}
                             onValueChange={(itemValue, itemIndex) => setGruposPicker1(itemValue)}
                            >
                                <Picker.Item label='Escolha um grupo' value='grupo'/>
                                <Picker.Item label={testePicker} value={testePicker}/>
                                <Picker.Item label={testePicker1} value={testePicker1}/>
                            </Picker>

                            <TouchableOpacity onPress={() => alert(gruposPicker1)}>
                                <Text>teste</Text>
                            </TouchableOpacity>
                        </View>
                 <View style={{flexDirection:'row',marginTop:'10%'}}>
                        <View style={{flexDirection:'row'}}>
                            <Text style={{fontSize:15, fontWeight:'bold', color:'#fff',marginLeft:'2%'}}>Nome</Text>
                            <Text style={{fontSize:15, fontWeight:'bold', color:'#fff', marginLeft:'10%'}}>Média</Text>
                        </View>
                        <View style={{flexDirection:'row'}}>
                            <Text style={{fontSize:15, fontWeight:'bold', color:'#fff',marginLeft:'21%'}}>Nome</Text>
                            <Text style={{fontSize:15, fontWeight:'bold', color:'#fff', marginLeft:'20%'}}>Média</Text>
                        </View>
                 </View>
                 
               <View style={{flexDirection:'row',alignItems:'center',justifyContent:'center',width:'95%'}}>
                        <FlatList
                        data={nomeRecebido}
                        renderItem={ ({item}) => 
                        <View style={{width:'70%'}}>
                            
                            <View style={{flexDirection:'row'}}>
                                <View style={{width:'85%'}}>
                                    
                                    <Text style={{fontSize:15, fontWeight:'bold', borderWidth:1, borderColor:'#fff',borderRightWidth:0}}>{item.nome}</Text> 
                                </View>
                                <View style={{width:'20%',}}>
                                    { (item.avaliado == true)? 
                                    <Text style={{fontSize:15,fontWeight:'bold', borderWidth:1, borderColor:'#fff',borderLeftWidth:0}}>2.5</Text> : <Text style={{fontSize:15,fontWeight:'bold', borderWidth:1, borderColor:'#fff',borderLeftWidth:0}}> -</Text>
                                        
                                    }
                                </View>
                        </View>
                            
                        </View>
                        }
                        keyExtractor={(item) => item.nome}
                        />

                       <FlatList
                        data={nomeRecebido}
                        renderItem={ ({item}) => 
                        <View >
                            
                            <View style={{flexDirection:'row'}}>
                                <View style={{width:'57%'}}>
                                    
                                    <Text style={{fontSize:16, fontWeight:'bold', borderWidth:1, borderColor:'#fff',borderRightWidth:0}}>{item.nome}</Text> 
                                </View>
                                <View style={{width:'18%',marginRight:-10}}>
                                    { (item.avaliado == true)? 
                                    <Text style={{fontSize:15,fontWeight:'bold', borderWidth:1, borderColor:'#fff',borderLeftWidth:0}}>1.5</Text> : <Text style={{fontSize:15,fontWeight:'bold', borderWidth:1, borderColor:'#fff',borderLeftWidth:0}}> -</Text>
                                        
                                    }
                                </View>
                        </View>
                            
                        </View>
                        }
                        keyExtractor={(item) => item.nome}
                        />
                </View>
           
                <Text style={{fontSize:18, fontWeight:'bold', marginBottom:'9%',marginLeft:'3%',color:'#fff',marginTop:20}}>Média do grupo: {modalMedia}</Text>
                {(modalFalta > 0) ? <Text style={{fontSize:18, fontWeight:'bold',marginLeft:'3%',color:'#fff', marginTop:-20,paddingTop:-5}}>Alunos não avaliados: {modalFalta}</Text> : <Text></Text>}
              </View>
              
              
            </View>
        </Modal>


   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#38a69d',
       },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewFlatList:{
        flex:4,
        margin:'5%',
        backgroundColor:'#fff',
        borderRadius:10,
        marginBottom:"5%"
    },
    viewBtn:{
        flex:1,
        alignItems:'center',
        marginBottom:-25
    },
    viewBtn2:{
        flex:1,
        alignItems:'center',
        marginBottom:-25
        
    },
    btnIniciar:{
        backgroundColor:'#fff',
        alignItems:'center',
        justifyContent:'center',
