import React from 'react';
import { View, Text, StyleSheet,TextInput, TouchableOpacity, FlatList } from 'react-native';

import * as Animatable from 'react-native-animatable';

import { useNavigation } from '@react-navigation/native';

export default function InserirAluno() {
    const navigation = useNavigation();
 return (
   <View style={styles.container}>
       < Animatable.View animation="fadeInLeft"  style={styles.viewTitle}>
           <Text style={styles.title}>Criar Grupo</Text>
       </Animatable.View>

       <View style={styles.viewInput}>
           <TextInput
           placeholder='Digite o nome do Aluno'
           style={styles.input}
           />

       </View>
       <View style={styles.viewInput2}>
            <TextInput
                placeholder='Idade'
                style={styles.input2}
                />

            <TouchableOpacity style={styles.btnAdd}>
                <Text style={styles.btnAddTxt}>Adicionar</Text>
            </TouchableOpacity>
       </View>

       <Text style={styles.alunosCad}>Alunos Cadastrados</Text>
        
       < Animatable.View animation="zoomIn" style={styles.viewRodape}>
         <View>
             <FlatList/>
         </View>
        
       </Animatable.View>

       <Animatable.View animation="lightSpeedIn" style={styles.viewBtnFinalizar}>
             <TouchableOpacity style={styles.btnFinalizar} onPress={() => navigation.navigate('PainelControle')}>
                 <Text style={styles.btnFinalizarTxt}>Finalizar</Text>
             </TouchableOpacity>
         </Animatable.View>
        

   </View>
  );
}

const styles = StyleSheet.create({
    container:{
     flex:1,
     backgroundColor:'#38a69d',
    },
    viewInput:{
        marginTop:'-20%',
        alignItems:'center',
    },
    viewInput2:{
        
        marginLeft:'13%',
        marginTop:10,
        flexDirection:'row'
    },
    input:{
        borderBottomWidth:1,
        borderBottomColor:'#fff',
        width:'75%',
        color:'#fff',
        fontSize:16,
        fontWeight:'bold'
    },
    input2:{
        borderBottomWidth:1,
        borderBottomColor:'#fff',
        width:'20%' ,
        marginBottom:5,
        color:'#fff',
        fontSize:16,
        fontWeight:'bold'
    },
    btnAdd:{
        backgroundColor:'#fff',
        width:'50%',
        height: 40,
        borderRadius:10,
        alignItems:'center',
        justifyContent:'center',
        marginLeft:50,
        marginTop:10
    },
    btnAddTxt:{
        fontSize:18,
        color:'#38a69d',
        fontWeight:'bold'
    },
    viewTitle:{
        flex:1,
        marginTop:'10%',
        alignItems:'center'
       
    },
    title:{
        fontSize:28,
        fontWeight:'bold',
        color:'#fff'
    },
    viewRodape:{
        flex:2,
        backgroundColor:'#fff',
        borderRadius:10,
        margin:'5%'
    },
    viewBtnFinalizar:{
        marginTop:5,
        marginBottom:'10%',
        alignItems:'center'
    },
    btnFinalizar:{
        backgroundColor:'#fff',
        width:'75%',
        height:40,
        borderRadius:10,
        alignItems:'center',
        justifyContent:'center'
    },
    btnFinalizarTxt:{
        fontSize:18,
        fontWeight:'bold',
        color:'#38a69d'
    },
    alunosCad:{
        textAlign:'center',
        marginTop:'5%',
        marginBottom:-15,
        fontSize:16,
        color:'#fff',
        fontWeight:'bold'
    }
})
